 <style>
    #loadingBox{position:fixed;z-index:100;top:50%;left:50%;width:100px;height:100px;margin:-60px auto auto -85px;padding:10px 30px;border: 1px solid #2A2A2A;color:#fff;text-align:center;border-radius:10px;background:rgba(0,0,0,.66);}
    #loadingBox p{}
    #loadingBox i{position:absolute;z-index:9;top:60%;left:50%;margin:-16px 0 0 -16px;display:inline-block;width:20px;height:20px;border:4px dotted #000;border-color:rgba(255,255,255,.1) rgba(255,255,255,.2) rgba(255,255,255,.4) rgba(255,255,255,.5);border-radius:300px;-webkit-animation:loadforever 1.5s infinite linear;}
	 #alertBox{position:fixed;z-index:100;top:50%;left:50%;width:100px;height:80px;margin:-60px auto auto -85px;padding:10px 30px;border: 1px solid #2A2A2A;color:#fff;text-align:center;border-radius:10px;background:rgba(0,0,0,.66);}
    #alertBox p{}
    @-webkit-keyframes loadforever{0%{-webkit-transform:rotate(0deg);}100%{-webkit-transform:rotate(360deg);}}
</style>
<section id="loadingBox" class="fullHtml" style="display: none;"><i></i><span >加载中...</span></section>
<section id="alertBox" class="fullHtml" style="display: none;"><span id="alert_msg">错误提示</span></section>